/*Table structure for table `wp_posts` */

DROP TABLE IF EXISTS `wp_posts`;

CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=54 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

/*Data for the table `wp_posts` */

insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2018-07-08 12:05:33','2018-07-08 12:05:33','Welcome to WordPress. This is your first post. Edit or delete it, then start writing!','Hello world!','','publish','open','open','','hello-world','','','2018-07-08 12:31:05','2018-07-08 12:31:05','',0,'http://localhost/film/?p=1',0,'post','',1),(13,1,'2018-07-08 12:05:33','2018-07-08 12:05:33','','Films Custom Fields','','publish','closed','closed','','acf_films-custom-fields','','','2018-07-08 12:06:53','2018-07-08 12:06:53','',0,'http://localhost/wpfilm_test/?post_type=acf&#038;p=13',0,'acf','',0),(14,1,'2018-07-08 13:35:24','2018-07-08 13:35:24','Everyone\'s favorite family of superheroes is back in \"Incredibles 2\"--but this time Helen (voice of Holly Hunter) is in the spotlight, leaving Bob (voice of Craig T. Nelson) at home with Violet (voice of Sarah Vowell) and Dash (voice of Huck Milner) to navigate the day-to-day heroics of \"normal\" life. It\'s a tough transistion for everyone, made tougher by the fact that the family is still unaware of baby Jack-Jack\'s emerging superpowers. When a new villain hatches a brilliant and dangerous plot, the family and Frozone (voice of Samuel L. Jackson) must find a way to work together again--which is easier said than done, even when they\'re all Incredible.','THE INCREDIBLES 2','','publish','closed','closed','','the-incredibles-2','','','2018-07-08 14:36:36','2018-07-08 14:36:36','',0,'http://localhost/wpfilm_test/?post_type=films&#038;p=14',0,'films','',0),(15,1,'2018-07-08 13:37:32','2018-07-08 13:37:32','Avengers: Infinity War is a 2018 American superhero film based on the Marvel Comics superhero team the Avengers, produced by Marvel Studios and distributed by Walt Disney Studios Motion Pictures. It is the sequel to 2012\'s The Avengers and 2015\'s Avengers: Age of Ultron, and the nineteenth film in the Marvel Cinematic Universe (MCU). The film is directed by Anthony and Joe Russo, written by Christopher Markus and Stephen McFeely, and features an ensemble cast including Robert Downey Jr., Chris Hemsworth, Mark Ruffalo, Chris Evans, Scarlett Johansson, Benedict Cumberbatch, Don Cheadle, Tom Holland, Chadwick Boseman, Paul Bettany, Elizabeth Olsen, Anthony Mackie, Sebastian Stan, Danai Gurira, Letitia Wright, Dave Bautista, Zoe Saldana, Josh Brolin, and Chris Pratt. In the film, the Avengers and the Guardians of the Galaxy attempt to stop Thanos from amassing the all-powerful Infinity Stones.\r\n\r\nThe film was announced in October 2014 as Avengers: Infinity War – Part 1. The Russo brothers came on board to direct in April 2015 and by May, Markus and McFeely had signed on to write the script for the film, which drew inspiration from Jim Starlin\'s 1991 The Infinity Gauntlet comic and Jonathan Hickman\'s 2013 Infinity comic. In 2016, Marvel shortened the title to Avengers: Infinity War. Filming began in January 2017 at Pinewood Atlanta Studios in Fayette County, Georgia, with a large cast consisting mostly of actors reprising their roles from previous MCU films. The production lasted until July 2017, shooting back-to-back with an untitled direct sequel. Additional filming took place in Scotland, England, the Downtown Atlanta area, and New York City. With an estimated budget in the range of $316–400 million, it is one of the most expensive films ever made.','AVENGERS INFINITY WAR','','publish','closed','closed','','avengers-infinity-war','','','2018-07-08 14:36:16','2018-07-08 14:36:16','',0,'http://localhost/wpfilm_test/?post_type=films&#038;p=15',0,'films','',0),(16,1,'2018-07-08 12:39:26','2018-07-08 12:39:26','','aveng-infinwar-logo','','inherit','open','closed','','aveng-infinwar-logo','','','2018-07-08 06:12:26','2018-07-08 06:12:26','',15,'http://localhost/wpfilm_test/wp-content/uploads/2018/07/aveng-infinwar-logo.jpg',0,'attachment','image/jpeg',0),(17,1,'2018-07-08 13:25:11','2018-07-08 13:25:11','Deadpool 2 is a 2018 American superhero film based on the Marvel Comics character Deadpool, distributed by 20th Century Fox. It is the eleventh installment in the X-Men film series, and a direct sequel to the 2016 film Deadpool. The film is directed by David Leitch from a script by Rhett Reese, Paul Wernick, and Ryan Reynolds, with Reynolds starring in the title role alongside Josh Brolin, Morena Baccarin, Julian Dennison, Zazie Beetz, T.J. Miller, Brianna Hildebrand, and Jack Kesy. In the film, Deadpool forms the team X-Force to protect a young mutant from the time-traveling soldier Cable.\r\n\r\nPlans for a sequel to Deadpool began before that film\'s release, and were confirmed in February 2016. Though the original creative team of Reynolds, Reese, Wernick, and director Tim Miller were quickly set to return for the second film, Miller left the project in October 2016 due to creative differences with Reynolds and was soon replaced by Leitch. An extensive casting search took place to fill the role of Cable, with Brolin ultimately cast. Filming took place in British Columbia, Canada, from June to October 2017. During filming, stunt woman Joi \"SJ\" Harris died in a motorcycle accident; the film is dedicated to her memory. The film\'s score is the first to receive a parental advisory warning for explicit content, and the soundtrack also includes the original song \"Ashes\" by Céline Dion.','DEADPOOL 2','','publish','closed','closed','','deadpool-2','','','2018-07-08 14:37:39','2018-07-08 14:37:39','',0,'http://localhost/wpfilm_test/?post_type=films&#038;p=17',0,'films','',0),(18,1,'2018-07-08 12:14:54','2018-07-08 12:14:54','','Deadpool_2_poster','','inherit','open','closed','','deadpool_2_poster','','','2018-07-08 06:14:54','2018-07-08 06:14:54','',17,'http://localhost/wpfilm_test/wp-content/uploads/2018/07/Deadpool_2_poster.jpg',0,'attachment','image/jpeg',0),(24,1,'2018-07-08 12:39:20','2018-07-08 13:39:20','','Film Listing','','publish','closed','closed','','film-listing','','','2018-07-08 09:06:24','2018-07-08 09:06:24','',0,'http://localhost/wpfilm_test/?page_id=24',0,'page','',0),(25,1,'2018-07-08 12:39:20','2018-07-08 13:39:20','ASDASDASDASD','tEST','','inherit','closed','closed','','24-revision-v1','','','2018-07-08 07:39:20','2018-07-08 07:39:20','',24,'http://localhost/wpfilm_test/index.php/24-revision-v1/',0,'revision','',0),(52,1,'2018-07-08 14:34:49','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2018-07-08 14:34:49','0000-00-00 00:00:00','',0,'http://localhost/wpfilms/?p=52',0,'post','',0),(31,1,'2018-07-08 12:41:54','2018-07-08 12:41:54','ASDASDASDASD','Film Listing','','inherit','closed','closed','','24-revision-v1','','','2018-07-08 08:41:54','2018-07-08 08:41:54','',24,'http://localhost/wpfilm_test/index.php/24-revision-v1/',0,'revision','',0),(32,1,'2018-07-08 12:46:58','2018-07-08 12:46:58','','Film Listing','','inherit','closed','closed','','24-revision-v1','','','2018-07-08 08:46:58','2018-07-08 08:46:58','',24,'http://localhost/wpfilm_test/24-revision-v1/',0,'revision','',0),(33,1,'2018-07-08 12:48:08','2018-07-08 12:48:08','','Films','','publish','closed','closed','','films','','','2018-07-08 08:48:08','2018-07-08 08:48:08','',0,'http://localhost/wpfilm_test/?p=33',1,'nav_menu_item','',0),(34,1,'2018-07-08 12:52:57','2018-07-08 12:52:57','[films_listing no_of_films=\'2\']','Film Listing','','inherit','closed','closed','','24-revision-v1','','','2018-07-08 08:52:57','2018-07-08 08:52:57','',24,'http://localhost/wpfilm_test/?p=34',0,'revision','',0),(35,1,'2018-07-08 12:54:13','2018-07-08 12:54:13','[films_listing]','Film Listing','','inherit','closed','closed','','24-revision-v1','','','2018-07-08 08:54:13','2018-07-08 08:54:13','',24,'http://local